// –•–µ–ª–ø–µ—Ä—ã –¥–ª—è Portable Text
function k() { return Math.random().toString(36).slice(2, 10) }

function textBlock(text, style = 'normal') {
  return {
    _type: 'block', _key: k(), style,
    markDefs: [],
    children: [{ _type: 'span', _key: k(), text, marks: [] }],
  }
}

function bold(text) {
  return { _type: 'span', _key: k(), text, marks: ['strong'] }
}

function normal(text) {
  return { _type: 'span', _key: k(), text, marks: [] }
}

function mixed(children, style = 'normal') {
  return {
    _type: 'block', _key: k(), style,
    markDefs: [],
    children,
  }
}

